#! /usr/bin/env node


'use strict';

var yargs = require('yargs');
var updateNotifier = require('update-notifier');
var readPkgUp = require('read-pkg-up');

var pkg = readPkgUp.sync({ cwd: __dirname }).pkg;
updateNotifier({
  pkg: pkg,
  updateCheckInterval: 1000 * 60 * 60 * 24 * 7 // 1 week
}).notify();

yargs.commandDir('commands').demand(1).help().strict().completion().argv;