'use strict';

var debug = require('debug');
var log = debug('cli:bootstrap');
var utils = require('../../utils');
log.error = debug('cli:bootstrap:error');

module.exports = {
  command: 'add <peer>',

  describe: 'Add peers to the bootstrap list',

  builder: {},

  handler: function handler(argv) {
    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }

      ipfs.bootstrap.add(argv.peer, function (err, list) {
        if (err) {
          throw err;
        }
      });
    });
  }
};