'use strict';

var utils = require('../../utils');
var debug = require('debug');
var log = debug('cli:bootstrap');
log.error = debug('cli:bootstrap:error');

module.exports = {
  command: 'list',

  describe: 'Show peers in the bootstrap list',

  builder: {},

  handler: function handler() {
    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }
      ipfs.bootstrap.list(function (err, list) {
        if (err) {
          throw err;
        }
        list.forEach(function (node) {
          console.log(node);
        });
      });
    });
  }
};