'use strict';

var debug = require('debug');
var path = require('path');
var log = debug('cli:config');
log.error = debug('cli:config:error');
var utils = require('../../utils');
var fs = require('fs');

module.exports = {
  command: 'replace <file>',

  describe: 'Replaces the config with <file>',

  builder: {},

  handler: function handler(argv) {
    if (argv._handled) return;
    argv._handled = true;

    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }

      var filePath = path.resolve(process.cwd(), argv.file);

      var config = utils.isDaemonOn() ? filePath : JSON.parse(fs.readFileSync(filePath, 'utf8'));

      ipfs.config.replace(config, function (err) {
        if (err) {
          throw err;
        }
      });
    });
  }
};