'use strict';

var utils = require('../../utils');
var debug = require('debug');
var log = debug('cli:object');
log.error = debug('cli:object:error');

module.exports = {
  command: 'data <key>',

  describe: 'Outputs the raw bytes in an IPFS object',

  builder: {},

  handler: function handler(argv) {
    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }

      ipfs.object.data(argv.key, { enc: 'base58' }, function (err, data) {
        if (err) {
          throw err;
        }

        console.log(data.toString());
      });
    });
  }
};