'use strict';

var utils = require('../../utils');
var debug = require('debug');
var log = debug('cli:object');
log.error = debug('cli:object:error');

module.exports = {
  command: 'links <key>',

  describe: 'Outputs the links pointed to by the specified object',

  builder: {},

  handler: function handler(argv) {
    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }

      ipfs.object.links(argv.key, { enc: 'base58' }, function (err, links) {
        if (err) {
          throw err;
        }

        links.forEach(function (link) {
          link = link.toJSON();
          console.log(link.Hash, link.Size, link.Name);
        });
      });
    });
  }
};