'use strict';

var utils = require('../../../utils');
var debug = require('debug');
var log = debug('cli:object');
var mDAG = require('ipfs-merkle-dag');
var DAGLink = mDAG.DAGLink;
log.error = debug('cli:object:error');

module.exports = {
  command: 'add-link <root> <name> <ref>',

  describe: 'Add a link to a given object',

  builder: {},

  handler: function handler(argv) {
    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }

      ipfs.object.get(argv.ref, { enc: 'base58' }).then(function (linkedObj) {
        var link = new DAGLink(argv.name, linkedObj.size(), linkedObj.multihash());
        return ipfs.object.patch.addLink(argv.root, link, { enc: 'base58' });
      }).then(function (node) {
        console.log(node.toJSON().Hash);
      }).catch(function (err) {
        throw err;
      });
    });
  }
};