'use strict';

var utils = require('../../../utils');
var bl = require('bl');
var fs = require('fs');
var debug = require('debug');
var log = debug('cli:object');
log.error = debug('cli:object:error');

function appendData(key, data) {
  utils.getIPFS(function (err, ipfs) {
    if (err) {
      throw err;
    }

    ipfs.object.patch.appendData(key, data, { enc: 'base58' }, function (err, node) {
      if (err) {
        throw err;
      }

      console.log(node.toJSON().Hash);
    });
  });
}

module.exports = {
  command: 'append-data <root> [data]',

  describe: 'Append data to the data segment of a dag node',

  builder: {},

  handler: function handler(argv) {
    if (argv.data) {
      return appendData(argv.root, fs.readFileSync(argv.data));
    }

    process.stdin.pipe(bl(function (err, input) {
      if (err) {
        throw err;
      }

      appendData(argv.root, input);
    }));
  }
};