'use strict';

var DAGLink = require('ipfs-merkle-dag').DAGLink;
var utils = require('../../../utils');
var debug = require('debug');
var log = debug('cli:object');
log.error = debug('cli:object:error');

module.exports = {
  command: 'rm-link <root> <link>',

  describe: 'Remove a link from an object',

  builder: {},

  handler: function handler(argv) {
    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }

      var dLink = new DAGLink(argv.link);

      ipfs.object.patch.rmLink(argv.root, dLink, { enc: 'base58' }, function (err, node) {
        if (err) {
          throw err;
        }

        console.log(node.toJSON().Hash);
      });
    });
  }
};