'use strict';

var utils = require('../../utils');
var bl = require('bl');
var fs = require('fs');
var debug = require('debug');
var log = debug('cli:object');
log.error = debug('cli:object:error');

function putNode(buf, enc) {
  utils.getIPFS(function (err, ipfs) {
    if (err) {
      throw err;
    }

    ipfs.object.put(buf, { enc: enc }, function (err, node) {
      if (err) {
        throw err;
      }

      console.log('added', node.toJSON().Hash);
    });
  });
}

module.exports = {
  command: 'put [data]',

  describe: 'Stores input as a DAG object, outputs its key',

  builder: {
    inputenc: {
      type: 'string',
      default: 'json'
    }
  },

  handler: function handler(argv) {
    if (argv.data) {
      return putNode(fs.readFileSync(argv.data), argv.inputenc);
    }

    process.stdin.pipe(bl(function (err, input) {
      if (err) {
        throw err;
      }

      putNode(input, argv.inputenc);
    }));
  }
};