'use strict';

var utils = require('../../utils');
var debug = require('debug');
var log = debug('cli:object');
log.error = debug('cli:object:error');

module.exports = {
  command: 'stat <key>',

  describe: 'Get stats for the DAG node named by <key>',

  builder: {},

  handler: function handler(argv) {
    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }

      ipfs.object.stat(argv.key, { enc: 'base58' }, function (err, stats) {
        if (err) {
          throw err;
        }

        delete stats.Hash; // only for js-ipfs-api output

        Object.keys(stats).forEach(function (key) {
          console.log(key + ': ' + stats[key]);
        });
      });
    });
  }
};