'use strict';

var utils = require('../../utils');
var debug = require('debug');
var log = debug('cli:object');
log.error = debug('cli:object:error');

module.exports = {
  command: 'addrs',

  describe: '',

  builder: function builder(yargs) {
    return yargs.commandDir('addrs');
  },
  handler: function handler(argv) {
    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }

      ipfs.swarm.addrs(function (err, res) {
        if (err) {
          throw err;
        }

        res.forEach(function (peer) {
          var count = peer.multiaddrs.length;
          console.log(peer.id.toB58String() + ' (' + count + ')');
          peer.multiaddrs.forEach(function (addr) {
            var res = addr.decapsulate('ipfs').toString();
            console.log('\t' + res);
          });
        });
      });
    });
  }
};