'use strict';

var utils = require('../utils');
var debug = require('debug');
var log = debug('cli:version');
log.error = debug('cli:version:error');

module.exports = {
  command: 'version',

  describe: 'Shows IPFS version information',

  builder: {
    number: {
      alias: 'n',
      type: 'boolean',
      default: false
    },
    commit: {
      type: 'boolean',
      default: false
    },
    repo: {
      type: 'boolean',
      default: false
    }
  },

  handler: function handler(argv) {
    // TODO: handle argv.{repo|commit|number}
    utils.getIPFS(function (err, ipfs) {
      if (err) {
        throw err;
      }
      ipfs.version(function (err, version) {
        if (err) {
          throw err;
        }

        console.log('js-ipfs version: ' + version.version);
      });
    });
  }
};