'use strict';

var series = require('async/series');
var Bitswap = require('ipfs-bitswap');

module.exports = function goOnline(self) {
  return function (cb) {
    series([self.load, self.libp2p.start], function (err) {
      if (err) {
        return cb(err);
      }

      self._bitswap = new Bitswap(self._libp2pNode.peerInfo, self._libp2pNode, self._repo.blockstore, self._libp2pNode.peerBook);
      self._bitswap.start();
      self._blockS.goOnline(self._bitswap);
      cb();
    });
  };
};