'use strict';

var pkg = require('../../../package.json');
var promisify = require('promisify-es6');

module.exports = function version(self) {
  return promisify(function (opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    callback(null, {
      version: pkg.version,
      repo: '',
      commit: ''
    });
  });
};